def FFT (sample_freq, data):
    
    # 执行FFT
    fft_result = np.fft.fft(data)
    N = len(data)
    
    # 对幅值做处理
    fft_result = [x/N*2 for x in fft_result]
    fft_result[0] /= 2
    
    freq = np.fft.fftfreq(N, 1/sample_freq)
    fft_result_half = fft_result[:N//2]
    return freq, fft_result


plt.plot(y2)
